<section>	<h1 class="title"><?=$info?><a class="link_add" href="<?=base_url();?>/administrator/images/?add" title="добавить"></a></h1><!--ALL-->	<?php if($act == 'all'):?>	<div id="gallery_box">			<?php if(count($images)):?>		<?php $i=1; foreach($images as $k):?>						<div style="padding:5px;margin:0 0 5px 0;display:inline-block;border:1px solid #ccc;height:100px;vertical-align:top;width:24.1%;position:relative;background:#f1f1f1;border-radius:2px;">				<a class="preview" style="float:left;margin:0 10px 0 0;" name="/img/for_page/<?=$k->url?>">					<img src="/img/for_page/mini/<?=$k->url?>">				</a>				<div style="font-size:10px;">					<b>(<?=$k->width?>px X <?=$k->height?>px)&nbsp;&nbsp;&nbsp; size: <?=$k->size?> kb.</b>					<br>					<br>					<b>&lt;img src="/img/for_page/<?=$k->url?>" alt=""&gt;</b>				</div>				<div style="position:absolute;right:5px;bottom:5px;">					<a class="link_del" onclick="if(confirm('УДАЛИТЬ /img/for_page/<?=$k->url?>')){window.location.href = '<?=base_url(); ?>/administrator/images/?del=<?=$k->url?>';}" title="удалить"></a>				</div>			</div>					<?php $i++; endforeach;?>		<?php endif;?>			</div>	<?php endif;?>	<!--ADD-->	<?php if($act == 'add'):?>	<div id="gallery_box">		<table id="gb">			<tr>				<td style="width:270px;">					<table>						<tr>							<td style="text-align:right;">								<b>водяной знак : </b>							</td>							<td>								<input type="checkbox" name="wm" value="1">							</td>						</tr>						<tr>							<td style="text-align:right;">								<b>ширина : </b>							</td>							<td>								<input type="text" name="width" value="">							</td>						</tr>						<tr>							<td style="text-align:right;">								<b>высота : </b>							</td>							<td>								<input type="text" name="height" value="">							</td>						</tr>						<tr>							<td style="text-align:center;padding:10px 0;" colspan="2">								<button class="butt_" onclick="uploadImages($(this))">Применить настройки</button>							</td>						</tr>					</table>				</td>				<td>					<div id="fileQueue"></div>					<div id="button_uploady" style="display:block;">						<form>							<input style="display:none;" id="file_upload" type="file" multiple="true" name="file_upload">						</form>					</div>				</td>			</tr>		</table>	</div>	<?php endif;?><script>function uploadImages(_this){	var c = '';	var cookies = document.cookie.split('; ');	for(var i = 0; i < cookies.length; i++){		var cookie = cookies[i].split('=');		if(cookie[0] == 'PHPSESSID'){			c = decodeURIComponent(cookie[1]);		}	}		_this.remove();	var width = $('input[name=width]').attr('disabled','disabled').val();	var height = $('input[name=height]').attr('disabled','disabled').val();	var wm = $('input[name=wm]:checked').val()||0;	$('input[name=wm]').attr('disabled','disabled');		$('#file_upload').uploadify({		'formData'     : {			'uploadify': ' ',			'images'   : ' ',			'height'   : height*1,			'width'    : width*1,			'wm'       : wm*1,			'session'  : c		},		'cancelImg'  : '<?=base_url(); ?>/uploadify/uploadify-cancel.png',		'queueID'    : 'fileQueue'	});}</script>		</section><!--CENTER-->